	continue:$e(str,1)="#"
	s type=$p(str,"*",1)
	continue:type=""
	for ii=2:1:$l(str,"*") {
		if $p(str,"*",ii)'="" {
			set @gn@("ext",$p(str,"*",ii))=type  
			set @gn@("typeext",type)=$p(str,"*",ii)
			set @gn@("type",$p(type,"/"),$p(str,"*",ii))=type
		}
	}
